package by.tasks.lin.main;

public class LinTask01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double x = 2;
		double y = 3;
		
		double sum;
		double raz;
		double pr;
		double chas;
		
		sum = x + y;
		raz = x - y;
		pr = x * y;
		chas = x / y;
		
		System.out.println("sum =" + sum + "\t raz= " + raz + "\t pr= " + pr + "\t chas= " + chas);
	}

}
