package by.tasks.lin.main;

public class LinTask12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x1 = 7;
		int x2 = 9;
		int y1 = 4;
		int y2 = 7;
		
		double ras;
		
		ras = Math.sqrt((Math.pow(x2, 2) - Math.pow(x1, 2)) + ((Math.pow(y2, 2) - Math.pow(y1, 2))));
		System.out.println("ras = " + ras);			
	}
}
