package by.tasks.lin.main;

public class LinTask14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int R = 9;
		double Pi = 3.14;
		double dlina;
		double S;
		
		dlina = ((2 * Pi) * R);
		S = Pi * Math.pow(R, 2);
		System.out.println("dlina = " + dlina + "\t S= " + S);
		
	}

}
