package by.tasks.lin.main;

public class LinTask15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double pi = 3.14;
		double pi1;
		double pi2;
		double pi3;
		double pi4;
		
		pi1 = pi * 1;
		pi2 = pi * pi;
		pi3 = Math.pow(pi, 3);
		pi4 = Math.pow(pi, 4);
		
		System.out.println("pi1 = " + pi1 + "\t pi2= " + pi2 + "\t pi3= " + pi3 + "\t pi4= " + pi4);
	}

}
