package by.tasks.lin.main;

public class LinTask18 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 5;
		int pl = 6;
		int S1;
		int S2;
		int V;
		
		S1 = (int)Math.pow(a, 2);
		S2 = S1 * pl;
		V = (int)Math.pow(a, 3);
		System.out.println("S1= " + S1 + "\t S2= " + S2 + "\t V= " + V);
	}

}
