package by.tasks.lin.main;

public class LinTask20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double dl = 9;
		double S;
		double R;
		double pi = 3.14;
		R = dl / (2 * pi);
		S = pi * Math.pow(R, 2);
		System.out.println("R = " + R + "\t S= " + S);
	}

}
